package com.jxbn.kaolatt.pay;import android.text.TextUtils;import java.util.Map;/** * 类名：PayResult * 描述： * 作者：lijunsai * 时间：2017/2/9 17:45 * 签名：你可能会认为你读得懂以下的代码。但是你不会懂的，相信我吧。 *      要是你尝试玩弄这段代码的话，你将会在无尽的通宵中不断地咒骂自己为什么会认为自己聪明到可以优化这段代码。 *      现在请关闭这个文件去玩点别的吧。 * sign：You may think you know what the following code does. *      But you dont. Trust me. *      Fiddle with it, and youll spend many a sleepless *      night cursing the moment you thought youd be clever *      enough to "optimize" the code below. *      Now close this file and go play with something else. */public class PayResult {	private String resultStatus;	private String result;	private String memo;	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}